namespace KadenceWP\KadenceWhiteLabel\StellarWP\Uplink\Contracts;

use KadenceWP\KadenceWhiteLabel\StellarWP\ContainerContract\ContainerInterface;
use KadenceWP\KadenceWhiteLabel\StellarWP\Uplink\Config;

abstract class Abstract_Provider implements Provider_Interface {

	/**
	 * @var ContainerInterface
	 */
	protected $container;

	/**
	 * Constructor for the class.
	 *
	 * @param ContainerInterface $container
	 */
	public function __construct( $container = null ) {
		$this->container = $container ?: Config::get_container();
	}

}
